import { db } from '../server/db.js';
import { controls } from '../shared/schema.js';

async function seedControls() {
  try {
    // Check if db is available
    if (!db) {
      console.error('Database not available');
      return;
    }

    // First check if controls already exist
    const existingControls = await db.select().from(controls);

    if (existingControls.length > 0) {
      console.log(`${existingControls.length} controls already exist. Skipping seeding.`);
      return;
    }

    // Insert controls for domain 1 (Cybersecurity Governance)
    const controlsToInsert = [
      {
        domainId: 1,
        name: 'Cybersecurity Strategy',
        controlId: 'ECC-1-1-1',
        description: 'The organization shall establish a cybersecurity strategy aligned with its business objectives and the national cybersecurity strategy.',
        guidance: 'Develop and document a cybersecurity strategy that includes specific goals, objectives, and priorities.',
        maturityLevel: 2,
        implementationGuide: 'Strategy should be approved by senior management and reviewed annually.'
      },
      {
        domainId: 1,
        name: 'Cybersecurity Policies',
        controlId: 'ECC-1-1-2',
        description: 'The organization shall establish and document cybersecurity policies, standards, and procedures.',
        guidance: 'Develop and maintain a comprehensive set of cybersecurity policies covering all relevant areas.',
        maturityLevel: 1,
        implementationGuide: 'Policies should be approved by management and reviewed at least annually.'
      },
      {
        domainId: 1,
        name: 'Cybersecurity Roles and Responsibilities',
        controlId: 'ECC-1-2-1',
        description: 'The organization shall define and document cybersecurity roles and responsibilities.',
        guidance: 'Clearly define the roles and responsibilities for cybersecurity throughout the organization.',
        maturityLevel: 1,
        implementationGuide: 'Roles should include a CISO or equivalent position with appropriate authority.'
      },
      {
        domainId: 1,
        name: 'Risk Management',
        controlId: 'ECC-1-3-1',
        description: 'The organization shall establish and implement a cybersecurity risk management program.',
        guidance: 'Develop and implement a risk management framework that identifies, assesses, and mitigates cybersecurity risks.',
        maturityLevel: 2,
        implementationGuide: 'Risk assessments should be conducted at least annually and when significant changes occur.'
      },
      {
        domainId: 1,
        name: 'Compliance Management',
        controlId: 'ECC-1-4-1',
        description: 'The organization shall establish and implement a cybersecurity compliance management program.',
        guidance: 'Identify and comply with applicable cybersecurity laws, regulations, and standards.',
        maturityLevel: 2,
        implementationGuide: 'Conduct regular compliance assessments and address any gaps.'
      }
    ];

    console.log('Inserting controls...');
    for (const control of controlsToInsert) {
      console.log(`Inserting control: ${control.name}`);

      // Insert the control
      await db.insert(controls).values({
        domainId: control.domainId,
        name: control.name,
        description: control.description,
        controlId: control.controlId,
        guidance: control.guidance,
        maturityLevel: control.maturityLevel,
        implementationGuide: control.implementationGuide,
        referenceLinks: null,
        order: 0  // Default order
      });
    }

    console.log('Seeding controls completed successfully!');
  } catch (error) {
    console.error('Error seeding controls:', error);
  } finally {
    process.exit(0);
  }
}

seedControls();

scripts/seed-db.ts.content:
import { db } from '../server/db';
import { frameworks, domains, controls } from '../shared/schema';

async function seedFrameworks() {
  try {
    console.log('Seeding frameworks, domains, and controls...');

    // Check if frameworks already exist
    const existingFrameworks = await db.select().from(frameworks);

    if (existingFrameworks.length > 0) {
      console.log('Frameworks already exist. Skipping seed.');
      return;
    }

    // NCA ECC Framework
    const [eccFramework] = await db.insert(frameworks).values({
      name: 'NCA Essential Cybersecurity Controls',
      displayName: 'NCA ECC',
      description: 'Saudi National Cybersecurity Authority Essential Cybersecurity Controls',
      version: '1.0',
      referenceUrl: 'https://nca.gov.sa',
      createdAt: new Date().toISOString(),
      updatedAt: new Date().toISOString(),
    }).returning();

    console.log('Created framework:', eccFramework.name);

    // ECC Domains
    const eccDomains = [
      {
        frameworkId: eccFramework.id,
        name: 'Cybersecurity Governance',
        displayName: 'Governance',
        order: 1,
        code: 'ECC-1',
        description: 'Controls related to cybersecurity governance and leadership',
      },
      {
        frameworkId: eccFramework.id,
        name: 'Cybersecurity Risk Management',
        displayName: 'Risk Management',
        order: 2,
        code: 'ECC-2',
        description: 'Controls related to risk management processes and procedures',
      },
      {
        frameworkId: eccFramework.id,
        name: 'Cybersecurity Operations',
        displayName: 'Operations',
        order: 3,
        code: 'ECC-3',
        description: 'Controls related to security operations and incident management',
      },
      {
        frameworkId: eccFramework.id,
        name: 'Technology Security',
        displayName: 'Technology',
        order: 4,
        code: 'ECC-4',
        description: 'Controls related to technical security measures and configurations',
      },
      {
        frameworkId: eccFramework.id,
        name: 'Third-Party Cybersecurity',
        displayName: 'Third-Party',
        order: 5,
        code: 'ECC-5',
        description: 'Controls related to managing third-party and vendor security',
      },
    ];

    // Insert domains
    for (const domainData of eccDomains) {
      const [domain] = await db.insert(domains).values(domainData).returning();
      console.log(`Created domain: ${domain.code} - ${domain.name}`);

      // Sample controls for each domain
      const domainControls = generateControlsForDomain(domain);

      // Insert controls
      for (const controlData of domainControls) {
        await db.insert(controls).values(controlData);
      }

      console.log(`Added ${domainControls.length} controls to domain ${domain.code}`);
    }

    // SAMA Framework
    const [samaFramework] = await db.insert(frameworks).values({
      name: 'SAMA Cyber Security Framework',
      displayName: 'SAMA CSF',
      description: 'Saudi Central Bank (SAMA) Cyber Security Framework',
      version: '1.0',
      referenceUrl: 'https://sama.gov.sa',
      createdAt: new Date().toISOString(),
      updatedAt: new Date().toISOString(),
    }).returning();

    console.log('Created framework:', samaFramework.name);

    // ISO 27001 Framework
    const [isoFramework] = await db.insert(frameworks).values({
      name: 'ISO/IEC 27001',
      displayName: 'ISO 27001',
      description: 'International Standard for Information Security Management Systems',
      version: '2022',
      referenceUrl: 'https://www.iso.org/standard/27001',
      createdAt: new Date().toISOString(),
      updatedAt: new Date().toISOString(),
    }).returning();

    console.log('Created framework:', isoFramework.name);

    // PDPL Framework
    const [pdplFramework] = await db.insert(frameworks).values({
      name: 'Saudi Arabia PDPL',
      displayName: 'SA PDPL',
      description: 'Saudi Arabia Personal Data Protection Law',
      version: '2021',
      referenceUrl: 'https://ndmo.gov.sa',
      createdAt: new Date().toISOString(),
      updatedAt: new Date().toISOString(),
    }).returning();

    console.log('Created framework:', pdplFramework.name);

    console.log('Seeding completed successfully!');
  } catch (error) {
    console.error('Error seeding frameworks:', error);
  } finally {
    process.exit(0);
  }
}

// Helper function to generate sample controls for a domain
function generateControlsForDomain(domain: any) {
  const controls = [];

  // Different number of controls per domain for more realistic data
  const numControls = 5 + Math.floor(Math.random() * 5); // 5-9 controls per domain

  for (let i = 1; i <= numControls; i++) {
    let controlName, controlDescription;

    switch (domain.code) {
      case 'ECC-1':
        controlName = `Cybersecurity Policy ${i}`;
        controlDescription = `Establish and maintain a comprehensive cybersecurity policy addressing organizational requirements.`;
        break;
      case 'ECC-2':
        controlName = `Risk Assessment ${i}`;
        controlDescription = `Conduct regular cybersecurity risk assessments to identify and manage risks.`;
        break;
      case 'ECC-3':
        controlName = `Security Monitoring ${i}`;
        controlDescription = `Implement continuous monitoring of security events and incidents.`;
        break;
      case 'ECC-4':
        controlName = `Access Control ${i}`;
        controlDescription = `Implement strong access control mechanisms for all systems and data.`;
        break;
      case 'ECC-5':
        controlName = `Vendor Management ${i}`;
        controlDescription = `Establish processes for assessing and managing third-party cybersecurity risks.`;
        break;
      default:
        controlName = `Control ${i}`;
        controlDescription = `Generic control description`;
    }

    controls.push({
      domainId: domain.id,
      controlId: `${domain.code}.${i}`,
      name: controlName,
      description: controlDescription,
      guidance: 'Follow industry best practices and organizational policies.',
      maturityLevel: 1 + Math.floor(Math.random() * 3), // 1-3
      implementationGuide: 'Follow industry best practices and organizational policies.',
    });
  }

  return controls;
}

seedFrameworks();

