                <QueryClientProvider client={queryClient}>
                        <CustomClerkProvider>
                                <AuthProvider>
                                        <HelmetProvider>
                                                <ThemeProvider>
                                                        <Router />
                                                        <Toaster />
                                                </ThemeProvider>
                                        </HelmetProvider>
                                </AuthProvider>
                        </CustomClerkProvider>
                </QueryClientProvider>
        );
}

export default App;

