import express, { Request, Response } from 'express';
import { storage } from '../storage';
import { insertAssessmentRiskSchema } from '@shared/schema';
import { z } from 'zod';

const router = express.Router();

// Get assessment risks by assessment ID
router.get('/assessment/:assessmentId', async (req: Request, res: Response) => {
  try {
    if (!req.isAuthenticated()) return res.sendStatus(401);

    const assessmentId = parseInt(req.params.assessmentId);
    const assessmentRisks = await storage.getAssessmentRisksByAssessmentId(assessmentId);

    res.json(assessmentRisks);
  } catch (error: any) {
    console.error('Error getting assessment risks:', error);
    res.status(500).json({ message: error.message });
  }
});

// Get assessment risk by ID
router.get('/:id', async (req: Request, res: Response) => {
  try {
    if (!req.isAuthenticated()) return res.sendStatus(401);

    const id = parseInt(req.params.id);
    const assessmentRisk = await storage.getAssessmentRiskById(id);

    if (!assessmentRisk) {
      return res.status(404).json({ message: 'Assessment risk not found' });
    }

    res.json(assessmentRisk);
  } catch (error: any) {
    console.error('Error getting assessment risk by ID:', error);
    res.status(500).json({ message: error.message });
  }
});

// Create or update assessment risk
router.post('/', async (req: Request, res: Response) => {
  try {
    if (!req.isAuthenticated()) return res.sendStatus(401);

    // Validate request body
    const validatedData = insertAssessmentRiskSchema.parse(req.body);

    // Save the assessment risk
    const assessmentRisk = await storage.saveAssessmentRisk(validatedData);

    res.status(201).json(assessmentRisk);
  } catch (error: any) {
    if (error instanceof z.ZodError) {
      res.status(400).json({ message: 'Validation error', errors: error.errors });
    } else {
      console.error('Error creating/updating assessment risk:', error);
      res.status(500).json({ message: error.message });
    }
  }
});

// Delete assessment risk
router.delete('/:id', async (req: Request, res: Response) => {
  try {
    if (!req.isAuthenticated()) return res.sendStatus(401);

    const id = parseInt(req.params.id);
    await storage.deleteAssessmentRisk(id);

    res.sendStatus(204);
  } catch (error: any) {
    console.error('Error deleting assessment risk:', error);
    res.status(500).json({ message: error.message });
  }
});

// Assign risks to an assessment
router.post('/assign', async (req: Request, res: Response) => {
  try {
    if (!req.isAuthenticated()) return res.sendStatus(401);

    const { assessmentId, riskIds } = req.body;

    if (!assessmentId || !Array.isArray(riskIds)) {
      return res.status(400).json({ message: 'Invalid request. assessmentId and riskIds array required.' });
    }

    const results = [];

    for (const riskId of riskIds) {
      try {
        // Check if the assignment already exists
        const existingAssessmentRisks = await storage.getAssessmentRisksByAssessmentId(assessmentId);
        const alreadyAssigned = existingAssessmentRisks.some(ar => ar.riskId === riskId);

        if (!alreadyAssigned) {
          const assessmentRisk = await storage.saveAssessmentRisk({
            assessmentId,
            riskId,
            status: 'to_assess'
          });

          results.push({
            success: true,
            assessmentRisk
          });
        } else {
          results.push({
            success: false,
            riskId,
            error: 'Risk already assigned to this assessment'
          });
        }
      } catch (error: any) {
        results.push({
          success: false,
          riskId,
          error: error.message
        });
      }
    }

    res.status(201).json({
      totalProcessed: riskIds.length,
      successCount: results.filter(r => r.success).length,
      failureCount: results.filter(r => !r.success).length,
      results
    });
  } catch (error: any) {
    console.error('Error assigning risks to assessment:', error);
    res.status(500).json({ message: error.message });
  }
});

// Update status of an assessment risk
router.patch('/:id/status', async (req: Request, res: Response) => {
  try {
    if (!req.isAuthenticated()) return res.sendStatus(401);

    const id = parseInt(req.params.id);
    const { status, notes, evidence } = req.body;

    if (!status || !['to_assess', 'in_progress', 'completed'].includes(status)) {
      return res.status(400).json({ message: 'Invalid status. Must be one of: to_assess, in_progress, completed' });
    }

    const assessmentRisk = await storage.getAssessmentRiskById(id);

    if (!assessmentRisk) {
      return res.status(404).json({ message: 'Assessment risk not found' });
    }

    const updatedAssessmentRisk = await storage.saveAssessmentRisk({
      ...assessmentRisk,
      status,
      notes: notes || assessmentRisk.notes,
      evidence: evidence || assessmentRisk.evidence,
      reviewedBy: status === 'completed' ? req.user?.id : assessmentRisk.reviewedBy,
      reviewedAt: status === 'completed' ? new Date().toISOString() : assessmentRisk.reviewedAt
    });

    res.json(updatedAssessmentRisk);
  } catch (error: any) {
    console.error('Error updating assessment risk status:', error);
    res.status(500).json({ message: error.message });
  }
});

export default router;

server/api/assessments.ts.content:
import { Router } from "express";
import { storage } from "../storage";
import { db } from "../db";
import { eq, sql } from "drizzle-orm";
import { insertAssessmentSchema, assessmentResults, frameworks } from "@shared/schema";
import { json } from "drizzle-orm/pg-core";

const router = Router();

// Get all assessments for the current user
router.get("/api/assessments", async (req, res) => {
  if (!req.isAuthenticated()) {
    return res.status(401).json({ error: "Unauthorized" });
  }

  try {
    // Get assessments with framework names
    const assessmentsWithFrameworks = await db.execute(sql`
      SELECT 
        a.*,
        f.name as "frameworkName"
      FROM 
        assessments a
      JOIN 
        frameworks f ON a.framework_id = f.id
      WHERE 
        a.company_id = ${req.user.companyId || 1}
      ORDER BY 
        a.updated_at DESC
    `);

    res.json(assessmentsWithFrameworks.rows);
  } catch (error) {
    console.error("Error fetching assessments:", error);
    res.status(500).json({ error: "Failed to fetch assessments" });
  }
});

// Get a specific assessment
router.get("/api/assessments/:id", async (req, res) => {
  if (!req.isAuthenticated()) {
    return res.status(401).json({ error: "Unauthorized" });
  }

  try {
    const id = parseInt(req.params.id);

    // Get assessment with framework name
    const [assessmentWithFramework] = await db.execute(sql`
      SELECT 
        a.*,
        f.name as "frameworkName"
      FROM 
        assessments a
      JOIN 
        frameworks f ON a.framework_id = f.id
      WHERE 
        a.id = ${id}
      AND
        a.company_id = ${req.user.companyId || 1}
    `);

    if (!assessmentWithFramework) {
      return res.status(404).json({ error: "Assessment not found" });
    }

    res.json(assessmentWithFramework);
  } catch (error) {
    console.error("Error fetching assessment:", error);
    res.status(500).json({ error: "Failed to fetch assessment" });
  }
});

// Create a new assessment
router.post("/api/assessments", async (req, res) => {
  if (!req.isAuthenticated()) {
    return res.status(401).json({ error: "Unauthorized" });
  }

  try {
    const parsedData = insertAssessmentSchema.parse({
      ...req.body,
      companyId: req.user.companyId || 1,
      createdBy: req.user.id,
    });

    const assessment = await storage.createAssessment(parsedData);

    // Get all controls for the framework
    const framework = await storage.getFrameworkById(parsedData.frameworkId);
    const domains = await storage.getDomainsByFrameworkId(parsedData.frameworkId);

    // For each domain, get controls and create assessment results
    for (const domain of domains) {
      const controls = await storage.getControlsByDomainId(domain.id);

      for (const control of controls) {
        // Create an assessment result for each control with default "not_implemented" status
        await storage.saveAssessmentResult({
          assessmentId: assessment.id,
          controlId: control.id,
          status: "not_implemented",
          evidence: null,
          recommendation: "",
          managementResponse: "",
          targetDate: null,
          updatedBy: req.user.id,
        });
      }
    }

    res.status(201).json({
      ...assessment,
      frameworkName: framework?.name || "Unknown Framework",
    });
  } catch (error) {
    console.error("Error creating assessment:", error);
    res.status(400).json({ error: "Failed to create assessment" });
  }
});

// Update an assessment status
router.patch("/api/assessments/:id", async (req, res) => {
  if (!req.isAuthenticated()) {
    return res.status(401).json({ error: "Unauthorized" });
  }

  try {
    const id = parseInt(req.params.id);
    const { status, score } = req.body;

    const assessment = await storage.updateAssessmentStatus(id, status, score);

    if (!assessment) {
      return res.status(404).json({ error: "Assessment not found" });
    }

    res.json(assessment);
  } catch (error) {
    console.error("Error updating assessment:", error);
    res.status(400).json({ error: "Failed to update assessment" });
  }
});

// Complete an assessment (set status to completed and calculate score)
router.post("/api/assessments/:id/complete", async (req, res) => {
  if (!req.isAuthenticated()) {
    return res.status(401).json({ error: "Unauthorized" });
  }

  try {
    const id = parseInt(req.params.id);
    const assessment = await storage.getAssessmentById(id);

    if (!assessment) {
      return res.status(404).json({ error: "Assessment not found" });
    }

    // Get all results for this assessment
    const results = await storage.getAssessmentResultsByAssessmentId(id);

    // Calculate score based on implementation status
    const totalControls = results.length;
    const implementedControls = results.filter(r => r.status === "implemented").length;
    const partiallyImplementedControls = results.filter(r => r.status === "partially_implemented").length;

    // Calculate score: implemented = 100%, partially = 50%, not implemented = 0%
    const score = ((implementedControls * 1.0) + (partiallyImplementedControls * 0.5)) / totalControls * 100;

    // Update assessment status and score
    const updatedAssessment = await storage.updateAssessmentStatus(
      id, 
      "completed",
      Math.round(score * 10) / 10 // Round to 1 decimal place
    );

    res.json(updatedAssessment);
  } catch (error) {
    console.error("Error completing assessment:", error);
    res.status(500).json({ error: "Failed to complete assessment" });
  }
});

export default router;

server/api/company-info.ts.content:
import express, { Request, Response } from "express";
import { storage } from "../storage";
// Use Express's built-in authentication check
const isAuthenticated = (req: Request, res: Response, next: Function) => {
  if (!req.isAuthenticated()) {
    return res.status(401).json({ error: "Authentication required" });
  }
  next();
};
import { log } from "../vite";
import multer from "multer";
import { insertCompanyInfoSchema } from "@shared/schema";
import { saveFileToDatabase, getFileById, deleteFile } from "../file-service";
import fs from "fs";
import path from "path";
import { z } from "zod";

const router = express.Router();

// Configure multer for logo uploads
const logoStorage = multer.diskStorage({
  destination: (req, file, cb) => {
    const dir = path.join(process.cwd(), "uploads", "logos");
    if (!fs.existsSync(dir)) {
      fs.mkdirSync(dir, { recursive: true });
    }
    cb(null, dir);
  },
  filename: (req, file, cb) => {
    const uniqueSuffix = Date.now() + "-" + Math.round(Math.random() * 1e9);
    const ext = path.extname(file.originalname);
    cb(null, `company-logo-${uniqueSuffix}${ext}`);
  }
});

const logoUpload = multer({
  storage: logoStorage,
  limits: {
    fileSize: 5 * 1024 * 1024, // 5MB
  },
  fileFilter: (req, file, cb) => {
    if (file.mimetype.startsWith("image/")) {
      cb(null, true);
    } else {
      cb(new Error("Only image files are allowed") as any);
    }
  }
});

// Configure multer for document uploads
const documentStorage = multer.diskStorage({
  destination: (req, file, cb) => {
    const dir = path.join(process.cwd(), "uploads", "company-docs");
    if (!fs.existsSync(dir)) {
      fs.mkdirSync(dir, { recursive: true });
    }
    cb(null, dir);
  },
  filename: (req, file, cb) => {
    const uniqueSuffix = Date.now() + "-" + Math.round(Math.random() * 1e9);
    const ext = path.extname(file.originalname);
    cb(null, `company-doc-${uniqueSuffix}${ext}`);
  }
});

const documentUpload = multer({
  storage: documentStorage,
  limits: {
    fileSize: 10 * 1024 * 1024, // 10MB
  }
});

// Get company information
router.get("/", isAuthenticated, async (req: Request, res: Response) => {
  try {
    const companyInfo = await storage.getCompanyInfo();

    if (!companyInfo) {
      return res.status(404).json({ message: "Company information not found" });
    }

    // Fetch logo file info if it exists
    let logoFile = null;
    if (companyInfo.logoId) {
      logoFile = await getFileById(companyInfo.logoId);
    }

    // Prepare response with logo URL if available
    const response = {
      ...companyInfo,
      logoUrl: logoFile ? `/api/files/${logoFile.id}` : null,
      logoFile
    };

    res.status(200).json(response);
  } catch (error) {
    log(`Error fetching company information: ${error}`, "company-api");
    res.status(500).json({ error: "Failed to fetch company information" });
  }
});

// Create or update company information
router.post("/", isAuthenticated, async (req: Request, res: Response) => {
  try {
    const companyData = req.body;

    // Validate the data
    const validatedData = insertCompanyInfoSchema.parse(companyData);

    // Save to database
    const savedCompanyInfo = await storage.saveCompanyInfo({
      ...validatedData,
      updatedBy: req.user?.id || 0
    });

    res.status(201).json(savedCompanyInfo);
  } catch (error) {
    if (error instanceof z.ZodError) {
      return res.status(400).json({ error: error.errors });
    }
    log(`Error saving company information: ${error}`, "company-api");
    res.status(500).json({ error: "Failed to save company information" });
  }
});

// Upload company logo
router.post("/logo", isAuthenticated, logoUpload.single("logo"), async (req: Request, res: Response) => {
  try {
    if (!req.file) {
      return res.status(400).json({ error: "No logo file uploaded" });
    }

    // Save file to database
    const savedFile = await saveFileToDatabase(req.file, "company-logo", req.user?.id || 0);

    // Get existing company info
    let companyInfo = await storage.getCompanyInfo();

    // Delete old logo file if it exists
    if (companyInfo && companyInfo.logoId) {
      await deleteFile(companyInfo.logoId);
    }

    // Update company info with new logo file ID
    companyInfo = await storage.saveCompanyInfo({
      ...(companyInfo || { companyName: "Default Company" }),
      logoId: savedFile,
      updatedBy: req.user?.id || 0
    });

    res.status(201).json({
      logoId: savedFile,
      logoUrl: `/api/files/${savedFile}`,
      companyInfo
    });
  } catch (error) {
    if (req.file && fs.existsSync(req.file.path)) {
      fs.unlinkSync(req.file.path); // Delete the file if there was an error
    }

    log(`Error uploading company logo: ${error}`, "company-api");
    res.status(500).json({ error: "Failed to upload company logo" });
  }
});

// Upload company document
router.post("/documents", isAuthenticated, documentUpload.single("document"), async (req: Request, res: Response) => {
  try {
    if (!req.file) {
      return res.status(400).json({ error: "No document file uploaded" });
    }

    // Save file to database
    const savedFile = await saveFileToDatabase(req.file, "company-document", req.user?.id || 0);

    // Get existing company info
    let companyInfo = await storage.getCompanyInfo();

    if (!companyInfo) {
      return res.status(404).json({ error: "Company information not found" });
    }

    // Add document ID to array
    const documentsFileIds = companyInfo.documentsFileIds ? [...companyInfo.documentsFileIds as number[]] : [];
    documentsFileIds.push(savedFile);

    // Update company info with new documents array
    companyInfo = await storage.saveCompanyInfo({
      ...companyInfo,
      documentsFileIds,
      updatedBy: req.user?.id || 0
    });

    res.status(201).json({
      documentId: savedFile,
      documentUrl: `/api/files/${savedFile}`,
      companyInfo
    });
  } catch (error) {
    if (req.file && fs.existsSync(req.file.path)) {
      fs.unlinkSync(req.file.path); // Delete the file if there was an error
    }

    log(`Error uploading company document: ${error}`, "company-api");
    res.status(500).json({ error: "Failed to upload company document" });
  }
});

// Get company documents
router.get("/documents", isAuthenticated, async (req: Request, res: Response) => {
  try {
    const companyInfo = await storage.getCompanyInfo();

    if (!companyInfo) {
      return res.status(404).json({ error: "Company information not found" });
    }

    // Get document files
    const documentsFileIds = companyInfo.documentsFileIds as number[] || [];
    const documents = [];

    for (const fileId of documentsFileIds) {
      const file = await getFileById(fileId);
      if (file) {
        documents.push({
          ...file,
          downloadUrl: `/api/company/documents/${fileId}/download`
        });
      }
    }

    res.status(200).json({ documents });
  } catch (error) {
    log(`Error fetching company documents: ${error}`, "company-api");
    res.status(500).json({ error: "Failed to fetch company documents" });
  }
});

// Download a specific company document
router.get("/documents/:id/download", isAuthenticated, async (req: Request, res: Response) => {
  try {
    const fileId = parseInt(req.params.id);
    if (isNaN(fileId)) {
      return res.status(400).json({ error: "Invalid file ID" });
    }

    const file = await getFileById(fileId);
    if (!file) {
      return res.status(404).json({ error: "Document not found" });
    }

    // Verify the file exists on disk
    if (!fs.existsSync(file.path)) {
      return res.status(404).json({ error: "Document file not found on disk" });
    }

    // Send the file for download
    res.download(file.path, file.originalName);
  } catch (error) {
    log(`Error downloading company document: ${error}`, "company-api");
    res.status(500).json({ error: "Failed to download company document" });
  }
});

// Delete a company document
router.delete("/documents/:id", isAuthenticated, async (req: Request, res: Response) => {
  try {
    const fileId = parseInt(req.params.id);
    if (isNaN(fileId)) {
      return res.status(400).json({ error: "Invalid file ID" });
    }

    // Get existing company info
    let companyInfo = await storage.getCompanyInfo();

    if (!companyInfo) {
      return res.status(404).json({ error: "Company information not found" });
    }

    // Remove document ID from array
    const documentsFileIds = companyInfo.documentsFileIds as number[] || [];
    const updatedDocumentsFileIds = documentsFileIds.filter(id => id !== fileId);

    // Update company info with new documents array
    companyInfo = await storage.saveCompanyInfo({
      ...companyInfo,
      documentsFileIds: updatedDocumentsFileIds,
      updatedBy: req.user?.id || 0
    });

    // Delete the file
    await deleteFile(fileId);

    res.status(200).json({ message: "Document deleted successfully", companyInfo });
  } catch (error) {
    log(`Error deleting company document: ${error}`, "company-api");
    res.status(500).json({ error: "Failed to delete company document" });
  }
});

export default router;

