import { useState, useEffect } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { OnboardingStep, UserProgress } from "@shared/schema";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { Card, CardContent, CardHeader, CardTitle, CardDescription, CardFooter } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Progress } from "@/components/ui/progress";
import { Loader2, CheckCircle2, ArrowRight, Award, BookOpen, Star } from "lucide-react";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/hooks/use-auth";

export function OnboardingWizard() {
  const { user } = useAuth();
  const [activeStep, setActiveStep] = useState<number>(0);
  const [activeTab, setActiveTab] = useState<string>("content");
  const { toast } = useToast();

  // Fetch onboarding steps
  const { data: steps, isLoading: stepsLoading } = useQuery<OnboardingStep[]>({
    queryKey: ["/api/onboarding/steps"],
    staleTime: 60 * 1000, // 1 minute
  });

  // Fetch user progress if user is logged in
  const { data: userProgress, isLoading: progressLoading } = useQuery<UserProgress[]>({
    queryKey: ["/api/onboarding/progress", user?.id],
    enabled: !!user?.id,
    staleTime: 30 * 1000, // 30 seconds
  });

  // Determine which step to start with based on user progress
  useEffect(() => {
    if (steps && userProgress) {
      const incompleteStep = findFirstIncompleteStep(steps, userProgress);
      if (incompleteStep !== -1) {
        setActiveStep(incompleteStep);
      }
    }
  }, [steps, userProgress]);

  // Save user progress mutation
  const updateProgressMutation = useMutation({
    mutationFn: async (progress: { stepId: number; completed: boolean; score?: number }) => {
      return apiRequest("POST", "/api/onboarding/progress", progress);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/onboarding/progress", user?.id] });
      queryClient.invalidateQueries({ queryKey: ["/api/gamification/user-stats"] });
    },
    onError: (error: Error) => {
      toast({
        title: "Error saving progress",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  // Find the first incomplete step
  const findFirstIncompleteStep = (steps: OnboardingStep[], progress: UserProgress[]): number => {
    if (!steps || !progress) return 0;

    // Map step IDs to their completion status
    const completionMap = progress.reduce((acc, curr) => {
      acc[curr.stepId] = curr.completed;
      return acc;
    }, {} as Record<number, boolean>);

    // Find first incomplete step
    for (let i = 0; i < steps.length; i++) {
      const stepId = steps[i].id;
      if (!completionMap[stepId]) {
        return i;
      }
    }

    return 0; // Default to first step if all completed
  };

  // Mark current step as complete and move to next step
  const completeStep = async (stepId: number, score?: number) => {
    if (!user) {
      toast({
        title: "Login Required",
        description: "Please login to save your progress",
        variant: "default",
      });
      return;
    }

    await updateProgressMutation.mutateAsync({
      stepId,
      completed: true,
      score,
    });

    if (steps && activeStep < steps.length - 1) {
      setActiveStep(activeStep + 1);
      setActiveTab("content"); // Reset to content tab for new step

      toast({
        title: "Step completed!",
        description: `Moving to ${steps[activeStep + 1]?.title}`,
        variant: "default",
      });
    } else {
      toast({
        title: "Onboarding Complete!",
        description: "You have completed all onboarding steps",
        variant: "default",
      });
    }
  };

  // Calculate overall progress
  const calculateProgress = (): number => {
    if (!steps || !userProgress) return 0;

    const completedSteps = userProgress.filter(p => p.completed).length;
    return Math.round((completedSteps / steps.length) * 100);
  };

  // Check if a step is completed
  const isStepCompleted = (stepId: number): boolean => {
    if (!userProgress) return false;
    const progress = userProgress.find(p => p.stepId === stepId);
    return progress?.completed || false;
  };

  // Loading state
  if (stepsLoading || progressLoading) {
    return (
      <div className="flex items-center justify-center h-64">
        <Loader2 className="h-8 w-8 animate-spin text-primary" />
      </div>
    );
  }

  // No steps found
  if (!steps || steps.length === 0) {
    return (
      <Card className="mb-6">
        <CardContent className="pt-6">
          <div className="flex flex-col items-center justify-center h-64">
            <p className="text-muted-foreground">No onboarding steps available</p>
          </div>
        </CardContent>
      </Card>
    );
  }

  const currentStep = steps[activeStep];

  return (
    <div className="space-y-4">
      {/* Progress bar */}
      <div className="mb-8">
        <div className="flex justify-between items-center mb-2">
          <h3 className="text-sm font-medium">Onboarding Progress</h3>
          <span className="text-sm text-muted-foreground">{calculateProgress()}%</span>
        </div>
        <Progress value={calculateProgress()} className="h-2" />
      </div>

      {/* Step list */}
      <div className="flex gap-2 overflow-x-auto pb-2 mb-6">
        {steps.map((step, index) => (
          <Button
            key={step.id}
            variant={activeStep === index ? "default" : "outline"}
            size="sm"
            className="flex items-center gap-2 whitespace-nowrap"
            onClick={() => setActiveStep(index)}
          >
            {isStepCompleted(step.id) ? (
              <CheckCircle2 className="h-4 w-4 text-green-500" />
            ) : (
              <span className="h-5 w-5 rounded-full bg-muted flex items-center justify-center text-xs">
                {index + 1}
              </span>
            )}
            {step.title}
          </Button>
        ))}
      </div>

      {/* Current step card */}
      <Card className="border border-border/40 shadow-sm">
        <CardHeader>
          <div className="flex justify-between items-center">
            <div>
              <CardTitle className="text-xl">{currentStep.title}</CardTitle>
              <CardDescription>{currentStep.description}</CardDescription>
            </div>
            <div className="flex items-center gap-2">
              <Badge variant="outline" className="flex items-center gap-1">
                <BookOpen className="h-3 w-3" />
                <span>{currentStep.estimatedDuration} min</span>
              </Badge>
              <Badge variant="outline" className="flex items-center gap-1">
                <Star className="h-3 w-3" />
                <span>{currentStep.points} points</span>
              </Badge>
            </div>
          </div>
        </CardHeader>

        <Tabs value={activeTab} onValueChange={setActiveTab} className="w-full">
          <div className="px-6">
            <TabsList className="grid w-full grid-cols-2">
              <TabsTrigger value="content">Content</TabsTrigger>
              <TabsTrigger value="quiz" disabled={currentStep.type !== 'quiz'}>
                Quiz
              </TabsTrigger>
            </TabsList>
          </div>

          <TabsContent value="content" className="p-0">
            <CardContent className="pt-6">
              <div className="prose prose-invert max-w-none">
                {currentStep?.content && typeof currentStep.content === 'object' && 'content' in currentStep.content ? (
                  <div dangerouslySetInnerHTML={{ __html: currentStep.content.content as string }} />
                ) : (
                  <p className="text-muted-foreground">Content not available</p>
                )}
              </div>
            </CardContent>
          </TabsContent>

          <TabsContent value="quiz" className="p-0">
            <CardContent className="pt-6">
              {currentStep?.content && currentStep.type === 'quiz' && typeof currentStep.content === 'object' && 'questions' in currentStep.content ? (
                <div className="space-y-4">
                  <p className="font-medium">Test your knowledge</p>
                  <p className="text-sm text-muted-foreground">
                    Complete this quiz to earn points and mark this step as complete.
                  </p>
                  {/* Quiz implementation would go here */}
                  <p className="text-sm text-muted-foreground italic">
                    Quiz content will be implemented in a future update.
                  </p>
                </div>
              ) : (
                <p className="text-muted-foreground">No quiz available for this step</p>
              )}
            </CardContent>
          </TabsContent>
        </Tabs>

        <CardFooter className="flex justify-between py-4">
          <Button
            variant="outline"
            onClick={() => activeStep > 0 && setActiveStep(activeStep - 1)}
            disabled={activeStep === 0}
          >
            Previous
          </Button>

          <div className="space-x-2">
            {!isStepCompleted(currentStep.id) ? (
              <Button 
                onClick={() => completeStep(currentStep.id)} 
                disabled={updateProgressMutation.isPending}
              >
                {updateProgressMutation.isPending && (
                  <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                )}
                Mark Complete
              </Button>
            ) : (
              <Button
                onClick={() => activeStep < steps.length - 1 && setActiveStep(activeStep + 1)}
                disabled={activeStep === steps.length - 1}
              >
                Next Step
                <ArrowRight className="ml-2 h-4 w-4" />
              </Button>
            )}
          </div>
        </CardFooter>
      </Card>

      {/* Badges section */}
      <Card>
        <CardHeader>
          <CardTitle className="text-base flex items-center gap-2">
            <Award className="h-5 w-5" />
            Available Badges
          </CardTitle>
          <CardDescription>
            Complete onboarding steps to earn badges
          </CardDescription>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-2 sm:grid-cols-4 gap-4">
            {/* Badge display would go here */}
            <div className="flex items-center justify-center h-24 bg-muted/30 rounded-md">
              <p className="text-sm text-muted-foreground">Badges loading...</p>
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/progress/security-.content:
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Progress } from "@/components/ui/progress";
import { Skeleton } from "@/components/ui/skeleton";
import { 
  CheckCircle2, 
  Clock, 
  AlertCircle,

  ChevronRight
} from "lucide-react";
import { format } from "date-fns";
import { useToast } from "@/hooks/use-toast";

interface TimelineStep {
  id: number;
  title: string;
  description: string | null;
  order: number;
  type: string;
  points: number;
  estimatedDuration: number;
  completed?: boolean;
  startedAt?: string | null;
  completedAt?: string | null;
  status?: 'not_started' | 'in_progress' | 'completed';
}

export function SecurityTimeline() {
  const [activeStep, setActiveStep] = useState<number | null>(null);
  const { toast } = useToast();

  const { data: timelineSteps, isLoading, error } = useQuery<TimelineStep[]>({
    queryKey: ['/api/progress/timeline'],
    refetchOnWindowFocus: false,
  });

  if (isLoading) {
    return (
      <div className="w-full space-y-4">
        {[1, 2, 3, 4, 5].map((i) => (
          <Skeleton key={i} className="w-full h-24" />
        ))}
      </div>
    );
  }

  if (error) {
    return (
      <Card>
        <CardContent className="p-6 text-center">
          <AlertCircle className="h-10 w-10 text-muted-foreground mx-auto mb-4" />
          <h3 className="text-lg font-semibold">Failed to load timeline</h3>
          <p className="text-muted-foreground">Please try again later</p>
        </CardContent>
      </Card>
    );
  }

  // Sort timeline steps by order
  const sortedSteps = timelineSteps?.sort((a, b) => a.order - b.order) || [];

  // Find the most recent in-progress step
  const currentStep = sortedSteps.find(step => step.status === 'in_progress') || 
                     sortedSteps.find(step => step.status === 'not_started') ||
                     sortedSteps[sortedSteps.length - 1];

  // Calculate overall progress
  const completedSteps = sortedSteps.filter(step => step.status === 'completed').length;
  const totalSteps = sortedSteps.length;
  const progressPercentage = totalSteps > 0 ? (completedSteps / totalSteps) * 100 : 0;

  return (
    <div className="space-y-6">
      <Card className="mb-6">
        <CardContent className="p-6">
          <div className="flex flex-col md:flex-row justify-between items-center gap-4 mb-4">
            <div>
              <h3 className="text-xl font-semibold">Security Journey Progress</h3>
              <p className="text-muted-foreground">
                {completedSteps} of {totalSteps} steps completed
              </p>
            </div>
            <Badge variant="outline" className="px-3 py-1">
              {progressPercentage.toFixed(0)}% Complete
            </Badge>
          </div>
          <Progress value={progressPercentage} className="h-2 mb-2" />
        </CardContent>
      </Card>

      <div className="space-y-4">
        {sortedSteps.map((step) => (
          <TimelineItem 
            key={step.id}
            step={step}
            isActive={activeStep === step.id}
            onClick={() => setActiveStep(activeStep === step.id ? null : step.id)}
          />
        ))}
      </div>
    </div>
  );
}

interface TimelineItemProps {
  step: TimelineStep;
  isActive: boolean;
  onClick: () => void;
}

function TimelineItem({ step, isActive, onClick }: TimelineItemProps) {
  const StatusIcon = {
    completed: () => <CheckCircle2 className="h-6 w-6 text-green-500" />,
    in_progress: () => <Clock className="h-6 w-6 text-amber-500" />,
    not_started: () => <Clock className="h-6 w-6 text-muted-foreground" />,
  }[step.status || 'not_started'];

  const formatTimeEstimate = (minutes: number) => {
    if (minutes < 60) {
      return `${minutes} min`;
    }
    const hours = Math.floor(minutes / 60);
    const remainingMinutes = minutes % 60;
    return remainingMinutes > 0 ? `${hours}h ${remainingMinutes}m` : `${hours}h`;
  };

  return (
    <Card className={`border-l-4 ${
      step.status === 'completed' ? 'border-l-green-500' :
      step.status === 'in_progress' ? 'border-l-amber-500' : 'border-l-muted-foreground'
    }`}>
      <CardContent className="p-0">
        <div 
          className="p-4 flex items-center cursor-pointer hover:bg-accent/50 transition-colors"
          onClick={onClick}
        >
          <div className="mr-4">
            <StatusIcon />
          </div>
          <div className="flex-1">
            <div className="flex justify-between items-center">
              <h3 className="font-medium">{step.title}</h3>
              <div className="flex items-center space-x-2">
                <Badge variant="outline" className="px-2">
                  {step.points} pts
                </Badge>
                <Badge variant="secondary" className="text-xs px-2">
                  {formatTimeEstimate(step.estimatedDuration)}
                </Badge>
                <ChevronRight className={`h-5 w-5 transition-transform ${isActive ? 'rotate-90' : ''}`} />
              </div>
            </div>
            <p className="text-sm text-muted-foreground">{step.type}</p>
          </div>
        </div>

        {isActive && (
          <div className="p-4 pt-0 border-t">
            {step.description && (
              <p className="text-sm mb-4">{step.description}</p>
            )}

            <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-4">
              {step.startedAt && (
                <div>
                  <p className="text-xs text-muted-foreground">Started</p>
                  <p className="text-sm">{format(new Date(step.startedAt), 'MMM d, yyyy')}</p>
                </div>
              )}

              {step.completedAt && (
                <div>
                  <p className="text-xs text-muted-foreground">Completed</p>
                  <p className="text-sm">{format(new Date(step.completedAt), 'MMM d, yyyy')}</p>
                </div>
              )}

              <div>
                <p className="text-xs text-muted-foreground">Status</p>
                <p className="text-sm capitalize">{step.status?.replace('_', ' ')}</p>
              </div>
            </div>

            <div className="flex justify-end space-x-2 mt-2">
              {step.status !== 'completed' && (
                <Button size="sm">
                  {step.status === 'not_started' ? 'Start' : 'Continue'}
                </Button>
              )}
            </div>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

client/src/components/reports/generate-r.content:
import { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Loader2, FileText, Share2 } from "lucide-react";
import { 
  Dialog, 
  DialogContent, 
  DialogDescription, 
  DialogFooter, 
  DialogHeader, 
  DialogTitle,
  DialogTrigger
} from "@/components/ui/dialog";
import { 
  Form, 
  FormControl, 
  FormField, 
  FormItem, 
  FormLabel, 
  FormDescription,
  FormMessage 
} from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Checkbox } from "@/components/ui/checkbox";
import { Textarea } from "@/components/ui/textarea";
import { z } from "zod";
import { zodResolver } from "@hookform/resolvers/zod";
import { useForm } from "react-hook-form";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";

const generateReportSchema = z.object({
  assessmentId: z.number(),
  title: z.string().min(1, "Title is required"),
  summary: z.string().optional(),
  isPublic: z.boolean().default(false),
  format: z.enum(["pdf", "html", "json"]).default("pdf"),
});

type GenerateReportFormValues = z.infer<typeof generateReportSchema>;

interface GenerateReportButtonProps {
  assessmentId: number;
  assessmentName: string;
}

export default function GenerateReportButton({
  assessmentId,
  assessmentName,
}: GenerateReportButtonProps) {
  const [open, setOpen] = useState(false);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const form = useForm<GenerateReportFormValues>({
    resolver: zodResolver(generateReportSchema),
    defaultValues: {
      assessmentId,
      title: `Compliance Report - ${assessmentName} - ${new Date().toLocaleDateString()}`,
      summary: `Comprehensive compliance report for ${assessmentName}`,
      isPublic: false,
      format: "pdf",
    },
  });

  const generateReportMutation = useMutation({
    mutationFn: async (values: GenerateReportFormValues) => {
      const res = await apiRequest("POST", "/api/reports", values);
      return res.json();
    },
    onSuccess: (data) => {
      toast({
        title: "Report Generated",
        description: "Your compliance report has been generated successfully.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/reports/assessment", assessmentId] });
      setOpen(false);
      form.reset();
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: `Failed to generate report: ${error.message}`,
        variant: "destructive",
      });
    },
  });

  function onSubmit(data: GenerateReportFormValues) {
    generateReportMutation.mutate(data);
  }

  return (
    <Dialog open={open} onOpenChange={setOpen}>
      <DialogTrigger asChild>
        <Button 
          variant="default" 
          className="flex items-center gap-2"
        >
          <FileText className="w-4 h-4" />
          Generate Report
        </Button>
      </DialogTrigger>
      <DialogContent className="sm:max-w-[425px]">
        <DialogHeader>
          <DialogTitle>Generate Compliance Report</DialogTitle>
          <DialogDescription>
            Create a comprehensive compliance report for this assessment.
          </DialogDescription>
        </DialogHeader>

        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4 pt-4">
            <FormField
              control={form.control}
              name="title"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Report Title</FormLabel>
                  <FormControl>
                    <Input {...field} />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="summary"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Summary</FormLabel>
                  <FormControl>
                    <Textarea {...field} />
                  </FormControl>
                  <FormDescription>
                    A brief description of the report
                  </FormDescription>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="format"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Report Format</FormLabel>
                  <FormControl>
                    <select
                      className="flex h-10 w-full rounded-md border border-input bg-background px-3 py-2 text-sm ring-offset-background file:border-0 file:bg-transparent file:text-sm file:font-medium placeholder:text-muted-foreground focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 disabled:cursor-not-allowed disabled:opacity-50"
                      {...field}
                    >
                      <option value="pdf">PDF</option>
                      <option value="html">HTML</option>
                      <option value="json">JSON</option>
                    </select>
                  </FormControl>
                  <FormDescription>
                    Select the format for your report
                  </FormDescription>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="isPublic"
              render={({ field }) => (
                <FormItem className="flex flex-row items-center space-x-3 space-y-0 rounded-md border p-4">
                  <FormControl>
                    <Checkbox
                      checked={field.value}
                      onCheckedChange={field.onChange}
                    />
                  </FormControl>
                  <div className="space-y-1 leading-none">
                    <FormLabel>Make this report public</FormLabel>
                    <FormDescription>
                      Public reports can be accessed without authentication
                    </FormDescription>
                  </div>
                </FormItem>
              )}
            />

            <DialogFooter>
              <Button
                type="submit"
                disabled={generateReportMutation.isPending}
                className="w-full mt-4"
              >
                {generateReportMutation.isPending ? (
                  <>
                    <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                    Generating...
                  </>
                ) : (
                  <>
                    <FileText className="mr-2 h-4 w-4" />
                    Generate Report
                  </>
                )}
              </Button>
            </DialogFooter>
          </form>
        </Form>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/reports/one-click-.content:
import { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { 
  Dialog, 
  DialogContent, 
  DialogDescription, 
  DialogFooter, 
  DialogHeader, 
  DialogTitle,
  DialogTrigger 
} from "@/components/ui/dialog";
import { Loader2, FileText, Share2, Copy, Check, ExternalLink } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { Input } from "@/components/ui/input";

interface OneClickReportButtonProps {
  assessmentId: number;
  assessmentName: string;
}

export default function OneClickReportButton({
  assessmentId,
  assessmentName,
}: OneClickReportButtonProps) {
  const [isGenerating, setIsGenerating] = useState(false);
  const [shareLink, setShareLink] = useState<string | null>(null);
  const [reportId, setReportId] = useState<number | null>(null);
  const [showDialog, setShowDialog] = useState(false);
  const [copySuccess, setCopySuccess] = useState(false);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Generate report mutation
  const generateReportMutation = useMutation({
    mutationFn: async () => {
      setIsGenerating(true);

      // Generate a default title based on assessment name and current date
      const title = `Compliance Report - ${assessmentName} - ${new Date().toLocaleDateString()}`;
      const summary = `Comprehensive compliance report for ${assessmentName}`;

      // Generate the report with default settings
      const reportRes = await apiRequest("POST", "/api/reports", {
        assessmentId,
        title,
        summary,
        isPublic: false,
        format: "pdf"
      });

      if (!reportRes.ok) {
        throw new Error("Failed to generate report");
      }

      const reportData = await reportRes.json();
      setReportId(reportData.id);

      // Create a share link for the report
      const shareRes = await apiRequest("POST", `/api/reports/${reportData.id}/share`, {});

      if (!shareRes.ok) {
        throw new Error("Failed to create share link");
      }

      const shareLinkData = await shareRes.json();

      // Generate the full shareable URL
      const baseUrl = window.location.origin;
      const fullShareLink = `${baseUrl}/shared-report/${shareLinkData.shareToken}`;

      return { report: reportData, shareLink: fullShareLink };
    },
    onSuccess: (data) => {
      setIsGenerating(false);
      setShareLink(data.shareLink);
      setShowDialog(true);

      toast({
        title: "Report Generated",
        description: "Your compliance report has been generated with a shareable link.",
      });

      queryClient.invalidateQueries({ queryKey: ["/api/reports/assessment", assessmentId] });
    },
    onError: (error: Error) => {
      setIsGenerating(false);

      toast({
        title: "Error",
        description: `Failed to generate report: ${error.message}`,
        variant: "destructive",
      });
    },
  });

  async function copyToClipboard(text: string) {
    try {
      await navigator.clipboard.writeText(text);
      setCopySuccess(true);
      setTimeout(() => setCopySuccess(false), 2000);

      toast({
        title: "Copied!",
        description: "Link copied to clipboard.",
      });
    } catch (err) {
      toast({
        title: "Error",
        description: "Failed to copy link.",
        variant: "destructive",
      });
    }
  }

  function handleGenerateReport() {
    generateReportMutation.mutate();
  }

  return (
    <>
      <Button 
        variant="default" 
        className="flex items-center gap-2"
        onClick={handleGenerateReport}
        disabled={isGenerating}
      >
        {isGenerating ? (
          <>
            <Loader2 className="w-4 h-4 animate-spin" />
            Generating One-Click Report...
          </>
        ) : (
          <>
            <Share2 className="w-4 h-4" />
            Generate Report with Link
          </>
        )}
      </Button>

      <Dialog open={showDialog} onOpenChange={setShowDialog}>
        <DialogContent className="sm:max-w-[500px]">
          <DialogHeader>
            <DialogTitle>Report Generated Successfully</DialogTitle>
            <DialogDescription>
              Your compliance report has been generated. Use the shareable link below to share it with others.
            </DialogDescription>
          </DialogHeader>

          <div className="mt-4">
            <div className="flex items-center gap-2 mb-2">
              <FileText className="w-4 h-4 text-primary" />
              <span className="font-medium">Shareable Link</span>
            </div>

            <div className="flex items-center gap-2">
              <Input 
                value={shareLink || ""} 
                readOnly 
                className="flex-1"
              />
              <Button 
                variant="outline" 
                size="icon"
                onClick={() => shareLink && copyToClipboard(shareLink)}
              >
                {copySuccess ? (
                  <Check className="h-4 w-4 text-green-500" />
                ) : (
                  <Copy className="h-4 w-4" />
                )}
              </Button>
            </div>

            <p className="mt-2 text-sm text-muted-foreground">
              This link can be shared with anyone, no login required.
            </p>
          </div>

          <DialogFooter className="flex flex-col sm:flex-row gap-2 mt-4">
            {reportId && (
              <Button 
                variant="outline" 
                className="w-full sm:w-auto"
                asChild
              >
                <a 
                  href={`/reports/${reportId}`} 
                  target="_blank" 
                  rel="noopener noreferrer"
                >
                  <FileText className="mr-2 h-4 w-4" />
                  View Report
                </a>
              </Button>
            )}

            {shareLink && (
              <Button 
                variant="outline" 
                className="w-full sm:w-auto"
                asChild
              >
                <a 
                  href={shareLink} 
                  target="_blank" 
                  rel="noopener noreferrer"
                >
                  <ExternalLink className="mr-2 h-4 w-4" />
                  Open Shared Link
                </a>
              </Button>
            )}

            <Button 
              variant="default" 
              className="w-full sm:w-auto"
              onClick={() => setShowDialog(false)}
            >
              Done
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </>
  );
}

client/src/components/reports/reports-li.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardFooter, CardHeader, CardTitle } from "@/components/ui/card";
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Loader2, FileText, Download, Share2, Copy, Check, Calendar, ExternalLink } from "lucide-react";
import { ComplianceReport, ReportShareLink } from "@shared/schema";
import { Form, FormControl, FormDescription, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Switch } from "@/components/ui/switch";
import { z } from "zod";
import { zodResolver } from "@hookform/resolvers/zod";
import { useForm } from "react-hook-form";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { formatDistanceToNow } from "date-fns";

interface ReportsListProps {
  assessmentId: number;
}

const createShareLinkSchema = z.object({
  reportId: z.number(),
  expiresAt: z.string().optional(),
  password: z.string().optional(),
  maxViews: z.number().optional(),
});

type CreateShareLinkFormValues = z.infer<typeof createShareLinkSchema>;

export default function ReportsList({ assessmentId }: ReportsListProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [selectedReport, setSelectedReport] = useState<ComplianceReport | null>(null);
  const [shareLinkDialogOpen, setShareLinkDialogOpen] = useState(false);
  const [showPassword, setShowPassword] = useState(false);
  const [hasExpiry, setHasExpiry] = useState(false);
  const [hasMaxViews, setHasMaxViews] = useState(false);
  const [copySuccess, setCopySuccess] = useState(false);

  const { data: reports, isLoading } = useQuery<ComplianceReport[]>({
    queryKey: ["/api/reports/assessment", assessmentId],
    queryFn: async () => {
      const res = await fetch(`/api/reports/assessment/${assessmentId}`);
      if (!res.ok) throw new Error("Failed to load reports");
      return res.json();
    },
  });

  const { data: shareLinks, isLoading: isLoadingShareLinks } = useQuery<ReportShareLink[]>({
    queryKey: ["/api/reports/share", selectedReport?.id],
    queryFn: async () => {
      if (!selectedReport) return [];
      const res = await fetch(`/api/reports/${selectedReport.id}/share`);
      if (!res.ok) throw new Error("Failed to load share links");
      return res.json();
    },
    enabled: !!selectedReport,
  });

  const form = useForm<CreateShareLinkFormValues>({
    resolver: zodResolver(createShareLinkSchema),
    defaultValues: {
      reportId: selectedReport?.id || 0,
      expiresAt: undefined,
      password: undefined,
      maxViews: undefined,
    },
  });

  const createShareLinkMutation = useMutation({
    mutationFn: async (values: CreateShareLinkFormValues) => {
      const reportId = selectedReport?.id;
      if (!reportId) throw new Error("No report selected");

      // Format the values properly
      const payload: any = { ...values, reportId };

      // Only include expiresAt if hasExpiry is true
      if (!hasExpiry) {
        delete payload.expiresAt;
      }

      // Only include maxViews if hasMaxViews is true
      if (!hasMaxViews) {
        delete payload.maxViews;
      }

      const res = await apiRequest("POST", `/api/reports/${reportId}/share`, payload);
      return res.json();
    },
    onSuccess: (data) => {
      toast({
        title: "Share Link Created",
        description: "Your share link has been created successfully.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/reports/share", selectedReport?.id] });
      setShareLinkDialogOpen(false);
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: `Failed to create share link: ${error.message}`,
        variant: "destructive",
      });
    },
  });

  const deactivateShareLinkMutation = useMutation({
    mutationFn: async (linkId: number) => {
      const res = await apiRequest("POST", `/api/reports/share/${linkId}/deactivate`, {});
      return res.json();
    },
    onSuccess: (data) => {
      toast({
        title: "Share Link Deactivated",
        description: "The share link has been deactivated.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/reports/share", selectedReport?.id] });
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: `Failed to deactivate share link: ${error.message}`,
        variant: "destructive",
      });
    },
  });

  function onCreateShareLink(data: CreateShareLinkFormValues) {
    createShareLinkMutation.mutate(data);
  }

  function getReportLink(report: ComplianceReport) {
    return `/reports/${report.id}`;
  }

  function getShareLink(token: string, password?: string) {
    const baseUrl = window.location.origin;
    let url = `${baseUrl}/shared-report/${token}`;
    if (password) {
      url += `?password=${password}`;
    }
    return url;
  }

  async function copyToClipboard(text: string) {
    try {
      await navigator.clipboard.writeText(text);
      setCopySuccess(true);
      setTimeout(() => setCopySuccess(false), 2000);
      toast({
        title: "Copied!",
        description: "Link copied to clipboard.",
      });
    } catch (err) {
      toast({
        title: "Error",
        description: "Failed to copy link.",
        variant: "destructive",
      });
    }
  }

  function formatDate(dateString: string) {
    const date = new Date(dateString);
    return formatDistanceToNow(date, { addSuffix: true });
  }

  return (
    <div className="space-y-6">
      <h2 className="text-2xl font-bold">Compliance Reports</h2>

      {isLoading ? (
        <div className="flex items-center justify-center p-8">
          <Loader2 className="h-8 w-8 animate-spin text-muted-foreground" />
        </div>
      ) : reports && reports.length > 0 ? (
        <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
          {reports.map((report) => (
            <Card key={report.id} className="overflow-hidden">
              <CardHeader className="pb-3">
                <CardTitle className="text-lg">{report.title}</CardTitle>
                <CardDescription className="flex items-center gap-1">
                  <Calendar className="h-3 w-3" />
                  {formatDate(report.createdAt)}
                </CardDescription>
                <Badge 
                  variant={report.isPublic ? "outline" : "secondary"}
                  className="absolute top-2 right-2"
                >
                  {report.isPublic ? "Public" : "Private"}
                </Badge>
              </CardHeader>
              <CardContent>
                <p className="text-sm text-muted-foreground">{report.summary || "No summary provided."}</p>

                <div className="mt-3 flex items-center text-xs text-muted-foreground">
                  <Badge variant="outline">{report.format.toUpperCase()}</Badge>
                </div>
              </CardContent>
              <CardFooter className="flex justify-between gap-2">
                <Button variant="outline" size="sm" asChild>
                  <a href={getReportLink(report)} target="_blank" rel="noopener noreferrer">
                    <FileText className="mr-1 h-3 w-3" />
                    View
                  </a>
                </Button>
                <Button 
                  variant="outline" 
                  size="sm"
                  onClick={() => {
                    setSelectedReport(report);
                    setShareLinkDialogOpen(true);
                  }}
                >
                  <Share2 className="mr-1 h-3 w-3" />
                  Share
                </Button>
                <Button variant="outline" size="sm">
                  <Download className="mr-1 h-3 w-3" />
                  Download
                </Button>
              </CardFooter>
            </Card>
          ))}
        </div>
      ) : (
        <div className="rounded-md border border-dashed p-8 text-center">
          <h3 className="text-lg font-medium">No Reports Yet</h3>
          <p className="mt-2 text-sm text-muted-foreground">
            Generate a report to see it here.
          </p>
        </div>
      )}

      {/* Share Link Dialog */}
      <Dialog open={shareLinkDialogOpen} onOpenChange={setShareLinkDialogOpen}>
        <DialogContent className="sm:max-w-[500px]">
          <DialogHeader>
            <DialogTitle>Share Report</DialogTitle>
            <DialogDescription>
              Create a shareable link to this report that can be accessed without logging in.
            </DialogDescription>
          </DialogHeader>

          <Tabs defaultValue="create">
            <TabsList className="grid w-full grid-cols-2">
              <TabsTrigger value="create">Create New Link</TabsTrigger>
              <TabsTrigger value="manage">Manage Links</TabsTrigger>
            </TabsList>

            <TabsContent value="create">
              <Form {...form}>
                <form onSubmit={form.handleSubmit(onCreateShareLink)} className="space-y-3">
                  <div className="space-y-2">
                    <div className="flex items-center justify-between">
                      <FormLabel>Password Protection</FormLabel>
                      <Switch 
                        checked={showPassword} 
                        onCheckedChange={setShowPassword} 
                      />
                    </div>
                    {showPassword && (
                      <FormField
                        control={form.control}
                        name="password"
                        render={({ field }) => (
                          <FormItem>
                            <FormControl>
                              <Input 
                                type="text" 
                                placeholder="Enter password" 
                                {...field} 
                                value={field.value || ''}
                                onChange={(e) => field.onChange(e.target.value)}
                              />
                            </FormControl>
                            <FormDescription>
                              Viewers will need this password to access the report
                            </FormDescription>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                    )}
                  </div>

                  <div className="space-y-2">
                    <div className="flex items-center justify-between">
                      <FormLabel>Expiration Date</FormLabel>
                      <Switch 
                        checked={hasExpiry} 
                        onCheckedChange={setHasExpiry} 
                      />
                    </div>
                    {hasExpiry && (
                      <FormField
                        control={form.control}
                        name="expiresAt"
                        render={({ field }) => (
                          <FormItem>
                            <FormControl>
                              <Input 
                                type="datetime-local" 
                                {...field}
                                value={field.value || ''}
                                onChange={(e) => field.onChange(e.target.value)} 
                              />
                            </FormControl>
                            <FormDescription>
                              Link will expire at this date and time
                            </FormDescription>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                    )}
                  </div>

                  <div className="space-y-2">
                    <div className="flex items-center justify-between">
                      <FormLabel>Maximum Views</FormLabel>
                      <Switch 
                        checked={hasMaxViews} 
                        onCheckedChange={setHasMaxViews} 
                      />
                    </div>
                    {hasMaxViews && (
                      <FormField
                        control={form.control}
                        name="maxViews"
                        render={({ field }) => (
                          <FormItem>
                            <FormControl>
                              <Input 
                                type="number" 
                                min="1"
                                max="100"
                                {...field}
                                value={field.value || ''}
                                onChange={(e) => field.onChange(e.target.valueAsNumber)}
                              />
                            </FormControl>
                            <FormDescription>
                              Link will deactivate after this many views
                            </FormDescription>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                    )}
                  </div>

                  <DialogFooter className="pt-4">
                    <Button
                      type="submit"
                      disabled={createShareLinkMutation.isPending}
                      className="w-full"
                    >
                      {createShareLinkMutation.isPending ? (
                        <>
                          <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                          Creating...
                        </>
                      ) : (
                        <>
                          <Share2 className="mr-2 h-4 w-4" />
                          Create Share Link
                        </>
                      )}
                    </Button>
                  </DialogFooter>
                </form>
              </Form>
            </TabsContent>

            <TabsContent value="manage">
              {isLoadingShareLinks ? (
                <div className="flex items-center justify-center p-8">
                  <Loader2 className="h-8 w-8 animate-spin text-muted-foreground" />
                </div>
              ) : shareLinks && shareLinks.length > 0 ? (
                <div className="space-y-3">
                  {shareLinks.map((link) => (
                    <div 
                      key={link.id} 
                      className="rounded-md border p-3"
                    >
                      <div className="flex items-center justify-between">
                        <div className="flex flex-col">
                          <span className="text-sm font-medium">
                            Created {formatDate(link.createdAt)}
                          </span>
                          <div className="mt-1 flex items-center gap-2">
                            {link.isActive ? (
                              <Badge variant="outline" className="bg-green-50 text-green-600 border-green-200">
                                Active
                              </Badge>
                            ) : (
                              <Badge variant="outline" className="bg-red-50 text-red-600 border-red-200">
                                Inactive
                              </Badge>
                            )}

                            {link.password && (
                              <Badge variant="outline">
                                Password Protected
                              </Badge>
                            )}

                            {link.expiresAt && (
                              <Badge variant="outline">
                                Expires {formatDate(link.expiresAt)}
                              </Badge>
                            )}

                            {link.maxViews && (
                              <Badge variant="outline">
                                {link.viewCount}/{link.maxViews} Views
                              </Badge>
                            )}
                          </div>
                        </div>

                        <div className="flex items-center gap-2">
                          <Button 
                            variant="ghost" 
                            size="icon"
                            onClick={() => copyToClipboard(getShareLink(link.shareToken, link.password || undefined))}
                          >
                            {copySuccess ? (
                              <Check className="h-4 w-4 text-green-500" />
                            ) : (
                              <Copy className="h-4 w-4" />
                            )}
                          </Button>

                          <Button
                            variant="ghost"
                            size="icon"
                            asChild
                          >
                            <a 
                              href={`/shared-report/${link.shareToken}`} 
                              target="_blank" 
                              rel="noopener noreferrer"
                            >
                              <ExternalLink className="h-4 w-4" />
                            </a>
                          </Button>

                          {link.isActive && (
                            <Button
                              variant="destructive"
                              size="sm"
                              onClick={() => deactivateShareLinkMutation.mutate(link.id)}
                              disabled={deactivateShareLinkMutation.isPending}
                            >
                              {deactivateShareLinkMutation.isPending ? (
                                <Loader2 className="h-4 w-4 animate-spin" />
                              ) : (
                                "Deactivate"
                              )}
                            </Button>
                          )}
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="rounded-md border border-dashed p-8 text-center">
                  <h3 className="text-lg font-medium">No Share Links</h3>
                  <p className="mt-2 text-sm text-muted-foreground">
                    Create a share link to let others view this report.
                  </p>
                </div>
              )}
            </TabsContent>
          </Tabs>
        </DialogContent>
      </Dialog>
    </div>
  );
}

client/src/components/risk-management/ri.content:
import React, { useState } from "react";
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from "@/components/ui/card";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Tabs, TabsList, TabsTrigger, TabsContent } from "@/components/ui/tabs";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { PieChart, Pie, Cell, BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from "recharts";
import { Button } from "@/components/ui/button";
import { Download, Filter, Printer, RefreshCw, AlertTriangle, Loader2 } from "lucide-react";
import { useQuery } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { Link } from "wouter";

// Define the risk assessment entry type
type RiskAssessmentEntry = {
  domain: string;
  subdomain: string;
  domainCode: string;
  controlCode: string;
  controlName: string;
  controlStatus: "not_implemented" | "partially_implemented" | "implemented" | "not_applicable";
  currentStatus: string;
  recommendation: string;
  managementResponse: string;
  targetDate: string;
  priority: "High" | "Medium" | "Low";
};

// Assessment type
type Assessment = {
  id: number;
  name: string;
  startDate: string;
  status: string;
  companyId: number;
  frameworkId: number;
  createdBy: number;
  frameworkName: string;
  score?: number;
  completionDate?: string;
};

// Assessment result type
type AssessmentResult = {
  id: number;
  assessmentId: number;
  controlId: number;
  status: "not_implemented" | "partially_implemented" | "implemented" | "not_applicable";
  evidence: string | null;
  recommendation: string;
  managementResponse: string;
  targetDate: string | null;
  updatedBy: number;
  controlName: string;
  controlDescription: string;
  domainName: string;
  domainCode: string;
  controlCode: string;
  comments?: string | null;
};

// Create a mapping function to transform assessment results into risk assessment entries
const mapAssessmentResultsToEntries = (results: AssessmentResult[]): RiskAssessmentEntry[] => {
  return results.map(result => ({
    domain: result.domainName,
    subdomain: result.controlDescription,
    domainCode: result.domainCode,
    controlCode: result.controlCode,
    controlName: result.controlName,
    controlStatus: result.status,
    currentStatus: result.evidence || `No status details available for ${result.controlName}`,
    recommendation: result.recommendation || "No recommendation provided",
    managementResponse: result.managementResponse || "No response provided",
    targetDate: result.targetDate || "Not set",
    priority: calculatePriority(result)
  }));
};

// Calculate priority based on control attributes
const calculatePriority = (result: AssessmentResult): "High" | "Medium" | "Low" => {
  // For now, apply a simple rule-based approach
  // In a real app, this could use more sophisticated logic
  if (result.status === "not_implemented") {
    return "High"; 
  } else if (result.status === "partially_implemented") {
    return "Medium";
  } else {
    return "Low";
  }
};

// Format status for display
const formatStatus = (status: RiskAssessmentEntry["controlStatus"]): string => {
  switch (status) {
    case "not_implemented":
      return "Not Implemented";
    case "partially_implemented":
      return "Partially Implemented";
    case "implemented":
      return "Implemented";
    case "not_applicable":
      return "Not Applicable";
    default:
      return status;
  }
};

// Generate summary statistics
const generateSummaryStats = (data: RiskAssessmentEntry[]) => {
  const applicableEntries = data.filter(entry => entry.controlStatus !== "not_applicable");

  const domainStats: Record<string, { 
    total: number; 
    notImplemented: number; 
    partiallyImplemented: number; 
    implemented: number;
  }> = {};

  applicableEntries.forEach(entry => {
    if (!domainStats[entry.domain]) {
      domainStats[entry.domain] = { total: 0, notImplemented: 0, partiallyImplemented: 0, implemented: 0 };
    }

    domainStats[entry.domain].total++;

    if (entry.controlStatus === "not_implemented") {
      domainStats[entry.domain].notImplemented++;
    } else if (entry.controlStatus === "partially_implemented") {
      domainStats[entry.domain].partiallyImplemented++;
    } else if (entry.controlStatus === "implemented") {
      domainStats[entry.domain].implemented++;
    }
  });

  return {
    total: applicableEntries.length,
    notImplemented: applicableEntries.filter(e => e.controlStatus === "not_implemented").length,
    partiallyImplemented: applicableEntries.filter(e => e.controlStatus === "partially_implemented").length,
    implemented: applicableEntries.filter(e => e.controlStatus === "implemented").length,
    notApplicable: data.filter(e => e.controlStatus === "not_applicable").length,
    domainStats
  };
};

// Generate chart data
const generateChartData = (stats: ReturnType<typeof generateSummaryStats>) => {
  // For pie chart
  const pieData = [
    { name: "Not Implemented", value: stats.notImplemented, color: "#f87171" },
    { name: "Partially Implemented", value: stats.partiallyImplemented, color: "#facc15" },
    { name: "Implemented", value: stats.implemented, color: "#4ade80" }
  ];

  // For bar chart
  const barData = Object.entries(stats.domainStats).map(([domain, data]) => ({
    name: domain,
    "Not Implemented": data.notImplemented,
    "Partially Implemented": data.partiallyImplemented,
    "Implemented": data.implemented
  }));

  return { pieData, barData };
};

// Status badge component
const StatusBadge = ({ status }: { status: RiskAssessmentEntry["controlStatus"] }) => {
  const variants: Record<string, string> = {
    "not_implemented": "bg-red-500/20 text-red-500 border-red-500/20",
    "partially_implemented": "bg-amber-500/20 text-amber-500 border-amber-500/20",
    "implemented": "bg-green-500/20 text-green-500 border-green-500/20",
    "not_applicable": "bg-gray-500/20 text-gray-400 border-gray-400/20"
  };

  return (
    <Badge className={`${variants[status]} border`}>
      {formatStatus(status)}
    </Badge>
  );
};

export default function RiskAssessmentReport() {
  const [activeTab, setActiveTab] = useState("summary");
  const [selectedDomain, setSelectedDomain] = useState<string | null>(null);
  const [selectedPriority, setSelectedPriority] = useState<string | null>(null);
  const [selectedAssessmentId, setSelectedAssessmentId] = useState<number | null>(null);

  // Fetch assessments
  const { data: assessments, isLoading: isLoadingAssessments } = useQuery<Assessment[]>({
    queryKey: ["/api/assessments"],
    retry: 1,
  });

  // Get the latest assessment ID if none is selected
  const latestAssessmentId = selectedAssessmentId || 
    (assessments && assessments.length > 0 
      ? assessments.sort((a, b) => new Date(b.startDate).getTime() - new Date(a.startDate).getTime())[0].id 
      : null);

  // Fetch assessment results
  const { data: assessmentResults, isLoading: isLoadingResults } = useQuery<AssessmentResult[]>({
    queryKey: ["/api/assessment-results", latestAssessmentId],
    enabled: !!latestAssessmentId,
    retry: 1,
  });

  // Fetch risk prediction
  const { data: riskPrediction, isLoading: isLoadingPrediction } = useQuery({
    queryKey: ["/api/risk-prediction/assessment", latestAssessmentId],
    enabled: !!latestAssessmentId,
    retry: 1,
  });

  // Transform assessment results to risk assessment entries
  const riskAssessmentData: RiskAssessmentEntry[] = assessmentResults 
    ? mapAssessmentResultsToEntries(assessmentResults)
    : [];

  // Filter data based on selected filters
  const filteredData = riskAssessmentData.filter(entry => {
    if (selectedDomain && entry.domain !== selectedDomain) return false;
    if (selectedPriority && entry.priority !== selectedPriority) return false;
    return true;
  });

  // Generate statistics and chart data
  const stats = generateSummaryStats(filteredData);
  const { pieData, barData } = generateChartData(stats);

  // Get unique domains for filter
  const uniqueDomains = Array.from(new Set(riskAssessmentData.map(e => e.domain)));

  // Calculate compliance score
  const complianceScore = stats.total > 0 
    ? Math.round(((stats.implemented + stats.partiallyImplemented * 0.5) / stats.total) * 100)
    : 0;

  // High risks count from the risk prediction
  const highRisksCount = riskPrediction?.domain_risks
    ? riskPrediction.domain_risks.filter((domain: any) => domain.risk_level === "High").length
    : filteredData.filter(entry => entry.priority === "High").length;

  // Medium risks count
  const mediumRisksCount = riskPrediction?.domain_risks
    ? riskPrediction.domain_risks.filter((domain: any) => domain.risk_level === "Medium").length
    : filteredData.filter(entry => entry.priority === "Medium").length;

  // Determine if we're in loading state
  const isLoading = isLoadingAssessments || isLoadingResults || isLoadingPrediction;

  // Show loading state
  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="text-center">
          <Loader2 className="h-8 w-8 animate-spin mx-auto mb-4 text-primary" />
          <p className="text-muted-foreground">Loading risk assessment data...</p>
        </div>
      </div>
    );
  }

  // Show empty state if no assessments
  if (!assessments || assessments.length === 0) {
    return (
      <Card className="backdrop-blur-sm bg-card/50 border-primary/10 p-12">
        <div className="text-center">
          <AlertTriangle className="h-12 w-12 mx-auto text-muted-foreground opacity-50" />
          <h3 className="mt-4 text-xl font-medium">No Assessments Found</h3>
          <p className="text-muted-foreground mt-2">
            You haven't created any risk assessments yet.
          </p>
          <div className="flex justify-center mt-6">
            <Link href="/risk-assessment/new">
              <Button>Create Assessment</Button>
            </Link>
          </div>
        </div>
      </Card>
    );
  }

  // Show empty state if no results
  if (!assessmentResults || assessmentResults.length === 0) {
    return (
      <Card className="backdrop-blur-sm bg-card/50 border-primary/10 p-12">
        <div className="text-center">
          <AlertTriangle className="h-12 w-12 mx-auto text-muted-foreground opacity-50" />
          <h3 className="mt-4 text-xl font-medium">No Assessment Results Found</h3>
          <p className="text-muted-foreground mt-2">
            The selected assessment doesn't have any results yet.
          </p>
          <div className="flex justify-center mt-6">
            <Link href={`/risk-assessment/${latestAssessmentId}`}>
              <Button>Complete Assessment</Button>
            </Link>
          </div>
        </div>
      </Card>
    );
  }

  // Find the current assessment
  const currentAssessment = assessments?.find(a => a.id === latestAssessmentId);

  return (
    <div className="space-y-6">
      <Card className="backdrop-blur-sm bg-card/50 border-primary/10">
        <CardHeader>
          <div className="flex flex-col sm:flex-row sm:justify-between sm:items-center gap-4">
            <div>
              <CardTitle className="text-2xl font-bold">
                {currentAssessment?.frameworkName || "NCA ECC"} Risk Assessment Report
              </CardTitle>
              <CardDescription>
                Comprehensive analysis of compliance with Essential Cybersecurity Controls
              </CardDescription>
            </div>
            <div className="flex items-center gap-2">
              <Button variant="outline" size="sm" className="flex items-center gap-1">
                <Printer className="h-4 w-4" />
                <span className="hidden sm:inline">Print</span>
              </Button>
              <Button variant="outline" size="sm" className="flex items-center gap-1">
                <Download className="h-4 w-4" />
                <span className="hidden sm:inline">Export</span>
              </Button>
              <Button variant="outline" size="sm" className="flex items-center gap-1">
                <RefreshCw className="h-4 w-4" />
                <span className="hidden sm:inline">Refresh</span>
              </Button>
            </div>
          </div>
        </CardHeader>
        <CardContent>
          {assessments && assessments.length > 1 && (
            <div className="mb-6">
              <Select 
                value={latestAssessmentId?.toString() || ""} 
                onValueChange={(value) => setSelectedAssessmentId(parseInt(value))}
              >
                <SelectTrigger className="w-full sm:w-[300px]">
                  <SelectValue placeholder="Select Assessment" />
                </SelectTrigger>
                <SelectContent>
                  {assessments.map(assessment => (
                    <SelectItem key={assessment.id} value={assessment.id.toString()}>
                      {assessment.name} ({new Date(assessment.startDate).toLocaleDateString()})
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
          )}

          <div className="flex flex-col sm:flex-row items-start sm:items-center gap-4 mb-6">
            <div className="flex items-center gap-2">
              <Filter className="h-4 w-4 text-muted-foreground" />
              <span className="text-sm font-medium">Filters:</span>
            </div>
            <div className="grid grid-cols-1 sm:grid-cols-2 gap-4 w-full sm:w-auto">
              <Select value={selectedDomain || ""} onValueChange={(value) => setSelectedDomain(value || null)}>
                <SelectTrigger className="w-full sm:w-[200px]">
                  <SelectValue placeholder="All Domains" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="">All Domains</SelectItem>
                  {uniqueDomains.map(domain => (
                    <SelectItem key={domain} value={domain}>{domain}</SelectItem>
                  ))}
                </SelectContent>
              </Select>

              <Select value={selectedPriority || ""} onValueChange={(value) => setSelectedPriority(value || null)}>
                <SelectTrigger className="w-full sm:w-[200px]">
                  <SelectValue placeholder="All Priorities" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="">All Priorities</SelectItem>
                  <SelectItem value="High">High Priority</SelectItem>
                  <SelectItem value="Medium">Medium Priority</SelectItem>
                  <SelectItem value="Low">Low Priority</SelectItem>
                </SelectContent>
              </Select>
            </div>
          </div>

          <Tabs defaultValue="summary" value={activeTab} onValueChange={setActiveTab}>
            <TabsList className="grid w-full grid-cols-2">
              <TabsTrigger value="summary">Summary</TabsTrigger>
              <TabsTrigger value="detailed">Detailed Report</TabsTrigger>
            </TabsList>

            <TabsContent value="summary" className="py-4 space-y-6">
              <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                <Card>
                  <CardHeader className="pb-2">
                    <CardTitle className="text-lg">Overall Compliance</CardTitle>
                  </CardHeader>
                  <CardContent>
                    <div className="flex justify-center">
                      <div className="text-center">
                        <div className="text-4xl font-bold mb-2">{complianceScore}%</div>
                        <Progress value={complianceScore} className="h-2 w-[150px]" />
                        <p className="text-xs text-muted-foreground mt-2">
                          Based on {stats.total} applicable controls
                        </p>
                      </div>
                    </div>
                  </CardContent>
                </Card>

                <Card>
                  <CardHeader className="pb-2">
                    <CardTitle className="text-lg">Implementation Status</CardTitle>
                  </CardHeader>
                  <CardContent>
                    <div className="h-[150px]">
                      <ResponsiveContainer width="100%" height="100%">
                        <PieChart>
                          <Pie
                            data={pieData}
                            cx="50%"
                            cy="50%"
                            innerRadius={40}
                            outerRadius={70}
                            paddingAngle={2}
                            dataKey="value"
                            label={({ name, percent }) => `${name}: ${(percent * 100).toFixed(0)}%`}
                            labelLine={false}
                          >
                            {pieData.map((entry, index) => (
                              <Cell key={`cell-${index}`} fill={entry.color} />
                            ))}
                          </Pie>
                          <Tooltip />
                        </PieChart>
                      </ResponsiveContainer>
                    </div>
                  </CardContent>
                </Card>

                <Card>
                  <CardHeader className="pb-2">
                    <CardTitle className="text-lg">Status Breakdown</CardTitle>
                  </CardHeader>
                  <CardContent>
                    <div className="space-y-3">
                      <div>
                        <div className="flex justify-between mb-1">
                          <span className="text-sm">Not Implemented</span>
                          <span className="text-sm font-medium">{stats.notImplemented}</span>
                        </div>
                        <Progress value={(stats.notImplemented / stats.total) * 100} className="h-2 bg-red-100">
                          <div className="h-full bg-red-500 rounded-full"></div>
                        </Progress>
                      </div>
                      <div>
                        <div className="flex justify-between mb-1">
                          <span className="text-sm">Partially Implemented</span>
                          <span className="text-sm font-medium">{stats.partiallyImplemented}</span>
                        </div>
                        <Progress value={(stats.partiallyImplemented / stats.total) * 100} className="h-2 bg-amber-100">
                          <div className="h-full bg-amber-500 rounded-full"></div>
                        </Progress>
                      </div>
                      <div>
                        <div className="flex justify-between mb-1">
                          <span className="text-sm">Implemented</span>
                          <span className="text-sm font-medium">{stats.implemented}</span>
                        </div>
                        <Progress value={(stats.implemented / stats.total) * 100} className="h-2 bg-green-100">
                          <div className="h-full bg-green-500 rounded-full"></div>
                        </Progress>
                      </div>
                      {stats.notApplicable > 0 && (
                        <div>
                          <div className="flex justify-between mb-1">
                            <span className="text-sm">Not Applicable</span>
                            <span className="text-sm font-medium">{stats.notApplicable}</span>
                          </div>
                          <Progress value={(stats.notApplicable / (stats.total + stats.notApplicable)) * 100} className="h-2 bg-gray-100">
                            <div className="h-full bg-gray-400 rounded-full"></div>
                          </Progress>
                        </div>
                      )}
                    </div>
                  </CardContent>
                </Card>
              </div>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <Card>
                  <CardHeader className="pb-2">
                    <CardTitle className="text-lg">Implementation by Domain</CardTitle>
                  </CardHeader>
                  <CardContent>
                    <div className="h-[300px]">
                      <ResponsiveContainer width="100%" height="100%">
                        <BarChart
                          data={barData}
                          layout="vertical"
                          margin={{ top: 20, right: 30, left: 20, bottom: 5 }}
                        >
                          <CartesianGrid strokeDasharray="3 3" />
                          <XAxis type="number" />
                          <YAxis dataKey="name" type="category" width={150} />
                          <Tooltip />
                          <Legend />
                          <Bar dataKey="Not Implemented" stackId="a" fill="#f87171" />
                          <Bar dataKey="Partially Implemented" stackId="a" fill="#facc15" />
                          <Bar dataKey="Implemented" stackId="a" fill="#4ade80" />
                        </BarChart>
                      </ResponsiveContainer>
                    </div>
                  </CardContent>
                </Card>

                <Card>
                  <CardHeader className="pb-2">
                    <CardTitle className="text-lg">Risk Assessment Summary</CardTitle>
                  </CardHeader>
                  <CardContent>
                    <div className="space-y-4">
                      <div className="grid grid-cols-2 gap-4">
                        <div className="space-y-1">
                          <p className="text-sm text-muted-foreground">High Risks</p>
                          <p className="text-2xl font-bold text-red-500">{highRisksCount}</p>
                        </div>
                        <div className="space-y-1">
                          <p className="text-sm text-muted-foreground">Medium Risks</p>
                          <p className="text-2xl font-bold text-amber-500">{mediumRisksCount}</p>
                        </div>
                        <div className="space-y-1">
                          <p className="text-sm text-muted-foreground">Assessment Date</p>
                          <p className="text-sm">
                            {currentAssessment?.startDate 
                              ? new Date(currentAssessment.startDate).toLocaleDateString() 
                              : "Not available"}
                          </p>
                        </div>
                        <div className="space-y-1">
                          <p className="text-sm text-muted-foreground">Framework</p>
                          <p className="text-sm">{currentAssessment?.frameworkName || "NCA ECC"}</p>
                        </div>
                      </div>

                      {riskPrediction?.risk_summary && (
                        <div className="pt-2">
                          <h4 className="text-sm font-medium mb-1">Risk Analysis</h4>
                          <p className="text-sm text-muted-foreground">
                            {riskPrediction.risk_summary}
                          </p>
                        </div>
                      )}

                      {riskPrediction?.recommendations && (
                        <div className="pt-2">
                          <h4 className="text-sm font-medium mb-1">Key Recommendations</h4>
                          <ul className="text-sm text-muted-foreground list-disc list-inside">
                            {riskPrediction.recommendations.slice(0, 3).map((rec: string, index: number) => (
                              <li key={index} className="mb-1">{rec}</li>
                            ))}
                          </ul>
                        </div>
                      )}
                    </div>
                  </CardContent>
                </Card>
              </div>
            </TabsContent>

            <TabsContent value="detailed" className="py-4">
              <Card>
                <CardContent className="p-0 overflow-auto">
                  <Table>
                    <TableHeader className="bg-muted/50">
                      <TableRow>
                        <TableHead className="w-[180px]">Domain</TableHead>
                        <TableHead className="w-[100px]">Control ID</TableHead>
                        <TableHead className="min-w-[250px]">Control</TableHead>
                        <TableHead className="w-[150px]">Status</TableHead>
                        <TableHead className="min-w-[200px]">Current Status</TableHead>
                        <TableHead className="min-w-[200px]">Recommendation</TableHead>
                        <TableHead className="min-w-[150px]">Priority</TableHead>
                      </TableRow>
                    </TableHeader>
                    <TableBody>
                      {filteredData.length === 0 ? (
                        <TableRow>
                          <TableCell colSpan={7} className="h-24 text-center">
                            No results found.
                          </TableCell>
                        </TableRow>
                      ) : (
                        filteredData.map((entry, index) => (
                          <TableRow key={index}>
                            <TableCell className="font-medium">{entry.domain}</TableCell>
                            <TableCell>{entry.controlCode}</TableCell>
                            <TableCell>{entry.controlName}</TableCell>
                            <TableCell><StatusBadge status={entry.controlStatus} /></TableCell>
                            <TableCell className="max-w-[300px] truncate">{entry.currentStatus}</TableCell>
                            <TableCell className="max-w-[300px] truncate">{entry.recommendation}</TableCell>
                            <TableCell>
                              <Badge 
                                className={
                                  entry.priority === "High" 
                                    ? "bg-red-500/20 text-red-500 border-red-500/20" 
                                    : entry.priority === "Medium"
                                    ? "bg-amber-500/20 text-amber-500 border-amber-500/20"
                                    : "bg-blue-500/20 text-blue-500 border-blue-500/20"
                                }
                              >
                                {entry.priority}
                              </Badge>
                            </TableCell>
                          </TableRow>
                        ))
                      )}
                    </TableBody>
                  </Table>
                </CardContent>
              </Card>
            </TabsContent>
          </Tabs>
        </CardContent>
      </Card>
    </div>
  );
}

